n = int(input("Введите число: "))


def f(x):
    if x == 1:
        return 1

    sign = -1 ** (x + 1)
    return sign * f(x-1) / 2


seq = [f(x) for x in range(1, n+1)]
print(f"Сумма ряда {seq} = {sum(seq)}")
